import React from "react";
import { useState } from "react";
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';


const CreateNote = (props)=>{


const[val,updateval] = useState({

    text:"",
    content:""
})

const[demo,updatedemo] = useState()


    const update = (event)=>
    {

       const {name,value} = event.target;

       updateval((prevVal)=>{
                   
            return { ...prevVal, [name]:value }
       })

    }

    const submit = (event)=>{

        event.preventDefault();
 
          props.submit(val);

          updateval({text:"", content:""});
          
    }


 return(
      <>

                   <div   onClick={demofun} className="Gau"  style={{display:"flex" , justifyContent:"center",marginTop:"55px"}}>  
                  {demo?  <form>

                          <input className="input" type="text" name="text" placeholder="Text" onChange={update} value={val.text}/><br/>

                          <textarea className="input" rows="5" column="5" name="content" onChange={update} value={val.content} placeholder="Enter something"/>
                           <br/>

                           
                           <Button  variant="contained" color="primary" size="small" onClick={submit}  className="button" startIcon={<SaveIcon />} >Save  </Button>
                          
                       </form> : null }
                     </div>
      
</>

 );
}


export default CreateNote;
