import React from "react";
import { useState } from "react";


const CreateNote = (props)=>{


const[val,updateval] = useState({

    text:"",
    content:""
})


    const update = (event)=>
    {

       const {name,value} = event.target;

       updateval((prevVal)=>{
                   
            return { ...prevVal, [name]:value }
       })

    }

    const submit = (event)=>{

        event.preventDefault();
 
          props.submit(val);

          updateval({text:"", content:""});
          
    }


 return(
      <>

                   <div className="Gau"  style={{display:"flex" , justifyContent:"center",marginTop:"55px"}}>  
                       <form>

                          <input className="Gau" type="text" name="text" placeholder="Text" onChange={update} value={val.text}/><br/>

                          <textarea className="Gau" rows="5" column="5" name="content" onChange={update} value={val.content} placeholder="Enter something"/>
                           <br/>
                          <button className="Gau" onClick={submit}>submit</button> 
                       </form>
                     </div>
      
</>

 );
}


export default CreateNote;
