import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Notes from './Notes'

function App() {

  const [notes,updateNotes] = useState([]);

  const[demo,updatedemo] = useState(false);


  const addValue = (val)=>{

      updateNotes((prevVal)=>{

          return [...prevVal,val];

      })
  }

  const Delete = (id)=>{

    updateNotes((prevVal)=>{

       return prevVal.filter((input,index)=> index!=id)

    })
}

const demofun = (event)=>{

  event.preventDefault();

    updatedemo(false);

}

  return (
            <>
              <div  onDoubleClick={demofun}  style={{backgroundColor:"lightblue"}}> 
                <Header/>

                  <div style={{minHeight:"430px",backgroundColor:"lightblue"}}>

                  <CreateNote  submit={addValue}/> 

                  <span style={{display:"",marginRight:"70%"}}>
                   {notes.map((data,index)=>{

                     return (

                       <Notes   key={index}  id={index} value={data} onpress={Delete}/>

                       )

                   }) }
                   </span>

                  </div>

                <Footer/>
              </div>
            </>

          );
}

export default App;
