import React from "react";
import { useState } from "react";


const CreateNote = ()=>{


const[val,updateval] = useState({

    text:"",
    content:""
})


    const update = (event)=>
    {

       const {name,value} = event.target;

       updateval((prevVal)=>{
                   
            return { ...prevVal, [name]:value }
       })

    }


 return(
      <>

                   <div style={{display:"flex" , justifyContent:"center",marginTop:"55px"}}>  
                       <form>

                          <input type="text" name="text" placeholder="Text" onChange={update} value={val.text}/><br/>

                          <textarea rows="5" column="5" name="content" onChange={update} value={val.content} placeholder="Enter something"/>
                           <br/>
                          <button >submit</button> 
                       </form>
                     </div>
      
</>

 );
}


export default CreateNote;
