import React, { useState } from "react";

import Button from '@material-ui/core/Button';

import DeleteIcon from '@material-ui/icons/Delete';

const Notes = (props)=>{

    const[b , updateb] = useState(false);

    if(props.id %3==0)
    {
        updateb(!b);
    }

    const deleteFun = ()=>{

        props.onpress(props.id);
    }
   
    return(
                         <>

                         <span className="Notes" style={{backgroundColor:"rgb(224, 100, 197)",fontSize:"x-small"}} >
                            <span style={{padding:"5px" ,color:"rgb(68, 23, 23)"}}> # {props.value.text} </span>

                            <span h style={{ padding:"5px" , color:"rgb(68, 23, 23)"}} >* {props.value.content} </span>

                            <DeleteIcon className="del" style={{ marginLeft:"25px" , color:"rgb(221, 80, 80)"}} onClick={deleteFun}>  </DeleteIcon>
                         </span>

                        </>);

}

export default Notes;
